import { Fragment } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { Menu, Transition } from '@headlessui/react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const ProfileMenu = props => {
    return (
        <Menu as='div' className='relative inline-block text-left'>
            <Menu.Button className='flex items-center relative cursor-pointer'>
                {/*<img src={logo} className='mx-auto object-cover rounded-full w-12 mr-2' alt='profile' />*/}
                <FontAwesomeIcon icon='user-circle' className='fa-w-12 mr-2' />
                <span>{'Full Name'}</span>
            </Menu.Button>

            <Transition
                as={Fragment}
                enter='transition ease-out duration-100'
                enterFrom='transform opacity-0 scale-95'
                enterTo='transform opacity-100 scale-100'
                leave='transition ease-in duration-75'
                leaveFrom='transform opacity-100 scale-100'
                leaveTo='transform opacity-0 scale-95'
            >
                <Menu.Items className='absolute right-0 w-56 mt-2 origin-top-right bg-white divide-y divide-gray-100 rounded-md shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none'>
                    <div className='px-1 py-1 '>
                        <Link to={'#'}>
                            <Menu.Item>
                                {({ active }) => (
                                    <button
                                        className={`${
                                            active ? 'hover:bg-gray-100 text-black' : 'text-gray-900'
                                        } group flex rounded-md items-center w-full px-3 py-2 text-sm`}
                                    >
                                        <FontAwesomeIcon icon='cog' className='text-indigo-600 w-6 mr-3' />
                                        Settings
                                    </button>
                                )}
                            </Menu.Item>
                        </Link>
                    </div>
                    <div className='px-1 py-1'>
                        <Link to={'#'}>
                            <Menu.Item>
                                {({ active }) => (
                                    <button
                                        className={`${
                                            active ? 'hover:bg-gray-100 text-black' : 'text-gray-900'
                                        } group flex rounded-md items-center w-full px-3 py-3 text-sm`}
                                    >
                                        {/*<img src={logOut} className='w-6 ml-1 mr-2' alt='icon' />*/}
                                        <FontAwesomeIcon icon='sign-out-alt' className='text-gray-600 w-6 mr-3' />
                                        Log out
                                    </button>
                                )}
                            </Menu.Item>
                        </Link>
                    </div>
                </Menu.Items>
            </Transition>
        </Menu>
    );
};

ProfileMenu.propTypes = {};

export default ProfileMenu;
