import PropTypes from 'prop-types';
import Drawer from '../shared/Drawer/Drawer';
import Menu from '../shared/Menu/Menu';
import { ROUTES, DRAWER_SIDE } from '../../config/constants';

const MobileMenu = props => {
    const { isVisibleMobileMenuDrawer, openMobileMenuDrawer, closeMobileMenuDrawer } = props;

    return (
        <>
            <Drawer
                side={DRAWER_SIDE.left}
                className={'bg-indigo-600'}
                isVisible={isVisibleMobileMenuDrawer}
                openDrawer={openMobileMenuDrawer}
                closeDrawer={closeMobileMenuDrawer}
            >
                <Menu className={'px-3'}>
                    <Menu.Item
                        icon={'folder'}
                        isCollapsed={isVisibleMobileMenuDrawer}
                        link={ROUTES.dashboard.path}
                        className={'text-white hover:bg-white w-full'}
                        onClick={closeMobileMenuDrawer}
                    >
                        Dashboard
                    </Menu.Item>
                    <Menu.Item
                        icon={'folder'}
                        isCollapsed={isVisibleMobileMenuDrawer}
                        link={ROUTES.settings.path}
                        className={'text-white hover:bg-white w-full'}
                        closeEditDrawer={closeMobileMenuDrawer}
                    >
                        Settings
                    </Menu.Item>
                    <Menu.SubMenu
                        title={'Trade'}
                        icon={'folder-open'}
                        isCollapsed={isVisibleMobileMenuDrawer}
                        className={'text-white hover:bg-white w-full'}
                        childClass={'text-white '}
                    >
                        <Menu.Item
                            className={'text-white hover:bg-white w-full'}
                            isCollapsed={isVisibleMobileMenuDrawer}
                            closeEditDrawer={closeMobileMenuDrawer}
                            link={ROUTES.configurations.path}
                        >
                            Configurations
                        </Menu.Item>
                    </Menu.SubMenu>
                </Menu>
            </Drawer>
        </>
    );
};

MobileMenu.propTypes = {};

export default MobileMenu;
