import PropTypes from 'prop-types';
import Menu from '../Menu/Menu';

const Drawer = props => {
    const { isCollapsed, setIsCollapsed, children, ...drawerProps } = props;

    return (
        <div className='h-full w-full fixed z-50'>
            <div
                className={`bg-black  h-full w-full absolute right-0 top-0 z-40 transition-all duration-300 ease-in-out ${
                    isCollapsed ? 'opacity-50' : 'opacity-0 invisible'
                } `}
                onClick={() => setIsCollapsed(!isCollapsed)}
            />
            <div
                className={`bg-black w-9/12 h-screen space-y-6 py-7 px-2 absolute inset-y-0 left-0 transform  md:relative md:translate-x-0 transition duration-300 ease-in-out z-50 ${
                    isCollapsed ? '-translate-x-0' : '-translate-x-full'
                } `}
            >
                <div className='bg-indigo-600 text-white font-semibold text-2xl'>{children}</div>
            </div>
        </div>
    );
};

Drawer.propTypes = {};

export default Drawer;
