import React from 'react';
import PropTypes from 'prop-types';
import Input from '../Input/Input';

const Modal = props => {
    const { isShowModal, setIsShowModal, children, className, ...modalProps } = props;

    return (
        <div
            className={`w-full h-full fixed top-0 left-0 flex justify-center items-center transition-all duration-700 ease-in-out ${
                isShowModal ? 'hidden' : ''
            } ${className} `}
            {...modalProps}
        >
            <div
                className='w-full h-full absolute top-0 left-0 z-40 bg-black opacity-50 transition-all duration-700 ease-in-out'
                onClick={() => setIsShowModal(!isShowModal)}
            />
            <div
                className={`w-2/5 bg-white rounded shadow p-8 z-50 transform transition-all duration-500 delay-700  ${
                    !isShowModal ? 'scale-100' : 'scale-50'
                }`}
            >
                {children}
            </div>
        </div>
    );
};

Modal.propTypes = {};

export default Modal;
