import React, { Fragment, useEffect, useRef, useState } from 'react';
import PropTypes from 'prop-types';
import { Menu, Transition } from '@headlessui/react';
import logo from '../../assets/images/logo.svg';
import logOut from '../../assets/icons/MenuItemIcons/log-out.svg';
import config from '../../assets/icons/MenuItemIcons/config.svg';

const Dropdown = props => {
    const [show, setShow] = useState(true);
    const { className, children, ...popoverProps } = props;

    return (
        <Menu as='div' className='relative inline-block text-left'>
            <Menu.Button className='block relative cursor-pointer' onClick={() => setShow(!show)}>
                <img src={logo} className='mx-auto object-cover rounded-full w-12' alt='profile' />
            </Menu.Button>

            <Transition
                as={Fragment}
                enter='transition ease-out duration-100'
                enterFrom='transform opacity-0 scale-95'
                enterTo='transform opacity-100 scale-100'
                leave='transition ease-in duration-75'
                leaveFrom='transform opacity-100 scale-100'
                leaveTo='transform opacity-0 scale-95'
            >
                <Menu.Items className='absolute right-0 w-56 mt-2 origin-top-right bg-white divide-y divide-gray-100 rounded-md shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none'>
                    <div className='px-1 py-1 '>
                        <Menu.Item>
                            {({ active }) => (
                                <button
                                    className={`${
                                        active ? 'hover:bg-gray-100 text-black' : 'text-gray-900'
                                    } group flex rounded-md items-center w-full px-4 py-3 text-sm`}
                                >
                                    <img src={config} className='w-6 mr-2' alt='icon' />
                                    Settings
                                </button>
                            )}
                        </Menu.Item>
                    </div>

                    <div className='px-1 py-1'>
                        <Menu.Item>
                            {({ active }) => (
                                <button
                                    className={`${
                                        active ? 'hover:bg-gray-100 text-black' : 'text-gray-900'
                                    } group flex rounded-md items-center w-full px-4 py-3 text-sm`}
                                >
                                    <img src={logOut} className='w-6 ml-1 mr-2' alt='icon' />
                                    Log out
                                </button>
                            )}
                        </Menu.Item>
                    </div>
                </Menu.Items>
            </Transition>
        </Menu>
    );
};

Dropdown.propTypes = {};

export default Dropdown;

