import React, { useState } from 'react';
import PropTypes from 'prop-types';
import logo from '../../assets/images/logo.svg';
import arrowLeft from '../../assets/icons/Arrows/arrow-left.svg';
import arrowRight from '../../assets/icons/Arrows/arrow-right.svg';
import Menu from '../shared/Menu/Menu';

import menuIcon from '../../assets/icons/MenuItemIcons/menuItemIcon.svg'; // for test

const Sidebar = props => {
    const [toggle, setToggle] = useState(false);

    return (
        <div
            className={`h-full fixed bg-white pt-10 shadow transition-all duration-300 ease-in-out ${
                toggle ? 'w-20' : 'w-72'
            }`}
        >
            <img
                src={toggle ? arrowRight : arrowLeft}
                className={`w-8 border border-transparent rounded-full shadow cursor-pointer p-1 bg-white absolute  ml-4 transition-all duration-300 ease-in-out ${
                    toggle ? 'left-12' : 'left-64'
                }`}
                alt='icon'
                onClick={() => setToggle(!toggle)}
            />

            <div className='flex items-center justify-start px-4'>
                <img className='w-12 h-12' src={logo} alt='Logo' />
                <span
                    className={`text-gray-600 ml-4 text-2xl font-bold  transition-opacity delay-150 ${
                        toggle ? ' opacity-0 invisible' : ''
                    }`}
                >
                    Crypto Trader
                </span>
            </div>

            <Menu className={'mt-10 px-6'}>
                <Menu.Item icon={menuIcon} toggle={toggle}>
                    MenuItem1
                </Menu.Item>
                <Menu.SubMenu title={'SubMenu1'} icon={menuIcon} toggle={toggle}>
                    <Menu.Item toggle={toggle}>SubMenuItem1</Menu.Item>
                </Menu.SubMenu>
                <Menu.SubMenu title={'SubMenu1'} icon={menuIcon} toggle={toggle}>
                    <Menu.Item toggle={toggle}>SubMenuItem1</Menu.Item>
                </Menu.SubMenu>
            </Menu>
        </div>
    );
};

Sidebar.propTypes = {};

export default Sidebar;
