import React, { useState } from 'react';
import PropTypes from 'prop-types';

const Tooltip = props => {
    const [showTooltip, setShowTooltip] = useState(false);

    const { text, children, className, ...tooltipProps } = props;

    return (
        <div className={`bg-gray-700 text-white py-1 text-center w-36 rounded ${className}`} {...tooltipProps}>
            <span>{text}</span>
            <div className='relative' onMouseEnter={() => setShowTooltip(!showTooltip)}>
                {children}
            </div>
        </div>
    );
};

Tooltip.propTypes = {};

export default Tooltip;
