import { useState } from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const SubMenu = props => {
    const [isOpen, setIsOpen] = useState(true);

    const { title, icon, className, subTitleClass, childClass, children, isCollapsed, ...subMenuProps } = props;

    return (
        <div>
            <div
                className={`hover:text-gray-800 hover:bg-gray-100 cursor-pointer flex items-center p-2 mt-6 mb-2 transition-colors duration-300 rounded-lg  ${className} ${
                    !isOpen ? 'bg-gray-100 text-gray-800' : ''
                } ${isCollapsed ? 'w-9' : null}`}
                {...subMenuProps}
                onClick={() => setIsOpen(!isOpen)}
            >
                <FontAwesomeIcon icon={icon} alt='icon' className='w-6 fa-lg mx-auto' />
                <span className={`mx-4 text-lg font-normal ${subTitleClass}`}>{title}</span>
                <span className='flex-grow'>
                    <FontAwesomeIcon
                        icon={isOpen ? 'chevron-down' : 'chevron-up'}
                        // className={`float-right ${
                        //     isCollapsed ? ' opacity-0 invisible' : 'transition-opacity duration-300'
                        // }`}
                    />
                </span>
            </div>
            <div
                className={`border-l-2 border-gray-100 ml-4 pl-4 
                ${childClass} ${isOpen ? 'hidden' : ''} `}
            >
                {children}
            </div>
        </div>
    );
};

SubMenu.propTypes = {
    className: PropTypes.string,
};

export default SubMenu;
