    return (
        <div className={`bg-white shadow-xl p-5 flex items-center absolute top-2 ${className}`} {...messageProps}>
            <FontAwesomeIcon icon={'check-circle'} className='fa-lg text-green-500' />
            <span>{'message'}</span>
        </div>
    );
};

