import React from 'react';
import PropTypes from 'prop-types';
import { STATUS } from '../../../config/constants';

const Tag = props => {
    const { status, color, children, ...tagProps } = props;

    const TAG_STYLES = {
        [STATUS.active]: `border-${color}-500 bg-${color}-100`,
        // [STATUS.active]: `border-${color}-500 bg-${color}-100`,
    };

    return (
        <span className={`border py-1 px-2 text-sm ${TAG_STYLES[status]}`} {...tagProps}>
            {children}
        </span>
    );
};

Tag.propTypes = {};

export default Tag;
