import React from 'react';
import PropTypes from 'prop-types';
import Menu from '../../shared/Menu/Menu';
import { ROUTES } from '../../../config/constants';

const SidebarMenu = props => {
    const { isCollapsed } = props;

    return (
        <>
            <Menu className={'px-6'}>
                <Menu.Item
                    icon={'folder'}
                    link={ROUTES.dashboard.path}
                    className={'text-gray-400'}
                    itemTitleClass={`${
                        isCollapsed ? ' opacity-0 invisible' : 'transition-opacity opacity-100 visible duration-300'
                    }`}
                >
                    Dashboard
                </Menu.Item>
                <Menu.Item
                    icon={'folder'}
                    link={ROUTES.settings.path}
                    className={'text-gray-400'}
                    itemTitleClass={`${
                        isCollapsed ? ' opacity-0 invisible' : 'transition-opacity opacity-100 visible duration-300'
                    }`}
                >
                    Settings
                </Menu.Item>
                <Menu.SubMenu
                    title={'SubMenu1'}
                    icon={'folder-open'}
                    childClass={isCollapsed ? 'hidden' : ''}
                    subTitleClass={`${
                        isCollapsed ? ' opacity-0 invisible' : 'transition-opacity opacity-100 visible duration-300'
                    }`}
                >
                    <Menu.Item
                        itemTitleClass={`${
                            isCollapsed ? ' opacity-0 invisible' : 'transition-opacity opacity-100 visible duration-300'
                        }`}
                    >
                        SubMenuItem1
                    </Menu.Item>
                    <Menu.Item
                        itemTitleClass={`${
                            isCollapsed ? ' opacity-0 invisible' : 'transition-opacity opacity-100 visible duration-300'
                        }`}
                    >
                        SubMenuItem2
                    </Menu.Item>
                </Menu.SubMenu>
                <Menu.SubMenu
                    title={'SubMenu2'}
                    icon={'folder-open'}
                    childClass={isCollapsed ? 'hidden' : ''}
                    subTitleClass={`${
                        isCollapsed ? ' opacity-0 invisible' : 'transition-opacity opacity-100 visible duration-300'
                    }`}
                >
                    <Menu.Item
                        itemTitleClass={`${
                            isCollapsed ? ' opacity-0 invisible' : 'transition-opacity opacity-100 visible duration-300'
                        }`}
                    >
                        SubMenuItem3
                    </Menu.Item>
                </Menu.SubMenu>
            </Menu>
        </>
    );
};

SidebarMenu.propTypes = {};

export default SidebarMenu;
