import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import Menu from '../Menu/Menu';

const Item = props => {
    const { className, children, icon, ...itemProps } = props;
    return (
        <div
            className={`hover:text-gray-800 hover:bg-gray-100 cursor-pointer flex items-center p-2 my-6 transition-colors duration-200  text-gray-600 rounded-lg ${className}`}
            {...itemProps}
        >
            {icon ? <img src={icon} alt='icon' /> : null}
            <span className='mx-4 text-lg font-normal'>{children}</span>
        </div>
    );
};

MenuItem.propTypes = {
    className: PropTypes.string,
};

export default Menu.Item = Item;
