import React, { useState } from 'react';
import PropTypes from 'prop-types';

const Tooltip = props => {
    const [showTooltip, setShowTooltip] = useState(false);

    const { text, children, className, ...tooltipProps } = props;

    return (
        <div className='relative' onClick={() => setShowTooltip(!showTooltip)}>
            <span
                className={`bg-black opacity-70 text-white py-1 text-center w-36 absolute bottom-6 right-24 rounded border border-2 ${className}`}
                {...tooltipProps}
            >
                {'text'}
            </span>
            {children}
        </div>
    );
};

Tooltip.propTypes = {};

export default Tooltip;
