import { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import logo from '../../assets/images/logo.svg';
import Menu from '../shared/Menu/Menu';

const Sidebar = props => {
    const { isCollapsed, setIsCollapsed } = props;

    return (
        <div
            className={`h-screen pt-8 bg-red-300 shadow transition-all duration-300 ease-in-out ${
                isCollapsed ? 'w-24' : 'w-72'
            }`}
        >
            <FontAwesomeIcon
                icon={isCollapsed ? 'chevron-circle-right' : 'chevron-circle-left'}
                className={`fa-2x text-indigo-600 shadow-sm cursor-pointer mt-2 bg-white absolute  transition-all duration-300 ease-in-out ${
                    isCollapsed ? 'left-20' : 'left-64 ml-4'
                }`}
                onClick={() => setIsCollapsed(!isCollapsed)}
            />

            <div className='flex items-center justify-start px-4 ml-1 mb-10'>
                <img className='w-12 h-12' src={logo} alt='Logo' />
                <span
                    className={`text-gray-600 ml-14 text-2xl font-bold absolute transition-opacity delay-150 duration-300  ${
                        isCollapsed ? ' opacity-0 invisible' : ''
                    }`}
                >
                    Crypto Trader
                </span>
            </div>

            <Menu className={'px-6'}>
                <Menu.Item icon={'folder'} isCollapsed={isCollapsed}>
                    MenuItem1
                </Menu.Item>
                <Menu.SubMenu title={'SubMenu1'} icon={'folder-open'} isCollapsed={isCollapsed}>
                    <Menu.Item isCollapsed={isCollapsed}>SubMenuItem1</Menu.Item>
                    <Menu.Item isCollapsed={isCollapsed}>SubMenuItem2</Menu.Item>
                </Menu.SubMenu>
                <Menu.Item icon={'folder'} isCollapsed={isCollapsed}>
                    MenuItem2
                </Menu.Item>
                <Menu.SubMenu title={'SubMenu2'} icon={'folder-open'} isCollapsed={isCollapsed}>
                    <Menu.Item isCollapsed={isCollapsed}>SubMenuItem3</Menu.Item>
                </Menu.SubMenu>
            </Menu>
        </div>
    );
};

Sidebar.propTypes = {};

export default Sidebar;
