import PropTypes from 'prop-types';
import { useState } from 'react';
import Input from '../../../shared/Input/Input';
import Button from '../../../shared/Button/Button';
import { BUTTON_TYPES, ROUTES } from '../../../../config/constants';
import FormItem from '../../../shared/Form/FormItem/FormItem';
import { useForm } from 'react-hook-form';
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { deleteUser, updateUser } from '../../../../redux/actions/users';
import { setUserLoggedOut } from '../../../../redux/actions/auth';
import { deleteAutToken } from '../../../../utils/auth';

const Account = props => {
    const dispatch = useDispatch();
    const history = useHistory();

    const user = useSelector(state => state.auth.user);

    const {
        register: registerFormField,
        handleSubmit,
        reset,
        formState: { isDirty, dirtyFields },
    } = useForm({
        defaultValues: user,
    });

    const [isEditing, setIsEditing] = useState(false);

    const editAccount = () => {
        setIsEditing(isEditing => !isEditing);
    };

    const onSave = async values => {
        if (!isDirty) return;

        const changeUserData = Object.keys(dirtyFields).reduce((result, field) => ({ [field]: values[field] }), {});

        const updatedUser = await dispatch(updateUser({ id: user.id, ...changeUserData }));

        if (updatedUser) {
            reset(updatedUser);
        }
    };

    const onDeleteAccountClick = async () => {
        const deletedUser = await dispatch(deleteUser(user.id));

        if (deletedUser) {
            dispatch(setUserLoggedOut());
            deleteAutToken();
            history.push(ROUTES.login.path);
        }
    };

    return (
        <div className='h-full flex flex-col'>
            <h1 className='text-2xl text-gray-800 font-medium pb-6 mb-10 border-b border-gray-300'>Account Settings</h1>

            <form onSubmit={handleSubmit(onSave)}>
                <div className='w-full pr-8 xl:pr-14'>
                    {/*<div className='border-b border-gray-300 pb-12 mb-12'>*/}
                    <div className='bg-white p-5 rounded-md shadow mb-10'>
                        <h2 className='mb-7 text-xl text-gray-800'>Personal Information</h2>
                        <div className='flex-col'>
                            <div className='sm:flex mb-6'>
                                <div className='flex flex-col sm:w-1/2 sm:mr-6 mb-6 sm:mb-0'>
                                    <FormItem label={'First name'}>
                                        <Input
                                            type={'text'}
                                            placeholder={'First name'}
                                            className={'border-gray-200 text-gray-700 w-full'}
                                            disabled={!isEditing}
                                            {...registerFormField('first_name')}
                                        />
                                    </FormItem>
                                </div>
                                <div className='flex flex-col sm:w-1/2'>
                                    <FormItem label={'Last Name'}>
                                        <Input
                                            type={'text'}
                                            placeholder={'Last name'}
                                            className={'border-gray-200 text-gray-700 w-full'}
                                            disabled={!isEditing}
                                            {...registerFormField('last_name')}
                                        />
                                    </FormItem>
                                </div>
                            </div>
                            <div className='flex flex-col'>
                                <FormItem label={'Email'}>
                                    <Input
                                        type={'email'}
                                        placeholder={'Email'}
                                        className={'border-gray-200 text-gray-700 w-full'}
                                        disabled={!isEditing}
                                        {...registerFormField('email')}
                                    />
                                </FormItem>
                            </div>
                        </div>
                    </div>

                    <div className='bg-white p-5 rounded-md shadow mb-10'>
                        <h2 className='mb-7 text-xl text-black'>Binance API</h2>
                        <div className='sm:flex mb-7'>
                            <div className='flex flex-col sm:w-1/2 sm:mr-6 mb-6'>
                                <FormItem label={'API key'}>
                                    <Input
                                        type={'text'}
                                        placeholder={'API key'}
                                        className={'border-gray-200 text-gray-700 w-full'}
                                        disabled={!isEditing}
                                        {...registerFormField('api_key')}
                                    />
                                </FormItem>
                            </div>
                            <div className='flex flex-col sm:w-1/2'>
                                <FormItem label={'Secret Key'}>
                                    <Input
                                        type={'text'}
                                        placeholder={'Secret Key'}
                                        className={'border-gray-200 text-gray-700 w-full'}
                                        disabled={!isEditing}
                                        {...registerFormField('secret_key')}
                                    />
                                </FormItem>
                            </div>
                        </div>
                    </div>

                    <div className='flex justify-end'>
                        <Button
                            type={BUTTON_TYPES.primary}
                            className={'bg-red-600 hover:bg-red-700 focus:bg-red-700 w-40 justify-self-end'}
                            onClick={onDeleteAccountClick}
                        >
                            Delete Account
                        </Button>
                    </div>
                </div>
                <div className='border-t border-gray-300 pt-8 mt-9'>
                    <div className='pr-8 xl:pr-14 flex justify-end'>
                        {!isEditing && (
                            <Button type={'primary'} className={'w-56 rounded bg-indigo-700'} onClick={editAccount}>
                                Edit
                            </Button>
                        )}

                        <div className={`flex justify-end w-full xl:w-10/12 ${isEditing ? '' : 'hidden'}`}>
                            <Button type={'link'} onClick={editAccount}>
                                Discard Changes
                            </Button>
                            <Button
                                type={'primary'}
                                htmlType={'submit'}
                                className={'w-32 ml-6 lg:w-56 rounded bg-indigo-700'}
                                onClick={editAccount}
                            >
                                Save Changes
                            </Button>
                        </div>
                    </div>
                </div>
            </form>
        </div>
    );
};

Account.propTypes = {};

export default Account;
