import React from 'react';
import PropTypes from 'prop-types';
import Input from '../Input/Input';

const Modal = props => {
    const { isShowModal, setIsShowModal, children, className, ...modalProps } = props;

    return (
        <div className='w-full h-full fixed top-0 left-0 z-40 bg-black opacity-30'>
            <div
                className={`w-full h-full fixed top-0 left-0 z-40 bg-black opacity-30 flex justify-center items-center transition-all duration-300 ease-in-out ${
                    isShowModal ? 'opacity-50' : 'opacity-0 invisible'
                } ${className}`}
                onClick={() => setIsShowModal(!isShowModal)}
            />
            <div className='w-2/5 bg-white rounded shadow p-8 z-50'>{children}</div>
        </div>
    );
};

Modal.propTypes = {};

export default Modal;
