import React from 'react';
import PropTypes from 'prop-types';

const Popover = props => {
    const { className, children, ...popoverProps } = props;
    return (
        <ul
            className={`bg-white w-48 p-3 mt-5 absolute top-20 right-4 shadow-lg rounded-xl ${className}`}
            {...popoverProps}
        >
            {children}
        </ul>
    );
};

Popover.propTypes = {};

export default Popover;
