import PropTypes from 'prop-types';
import { useEffect } from 'react';
import useWindowSize from '../../hooks/useWindowSize';
import { Link, Switch, Route, useHistory, useLocation, Redirect } from 'react-router-dom';
import { BREAKPOINTS, ROUTES } from '../../config/constants';
import Tabs from '../shared/Tabs/Tabs';
import Account from './Section/Account/Account';
import Security from './Section/Security/Security';
import Telegram from './Section/Telegram/Telegram';
import Select from './Section/Account/Select/Select';

const Settings = props => {
    const history = useHistory();
    const location = useLocation();

    const { width: windowWidth } = useWindowSize();

    return (
        <div className='lg:flex w-full lg:h-full lg:pb-0 pb-0 lg:pr-0 pr-0 text-gray-800'>
            {windowWidth > BREAKPOINTS.lg ? (
                <div className='w-4/12 h-full p-10 lg:pl-12 lg:pr-0 pr-0 border-r border-gray-300'>
                    <h1 className='text-2xl text-gray-800 font-medium pb-6 mb-10 border-b border-gray-300'>Settings</h1>
                    <Tabs className={'text-gray-600'}>
                        <Link to={ROUTES.settingsAccount.path}>
                            <Tabs.Pane
                                icon={'user-circle'}
                                className={'hover:text-gray-900 pb-6 border-b border-gray-300'}
                            >
                                Account Settings
                            </Tabs.Pane>
                        </Link>
                        <Link to={ROUTES.settingsSecurity.path}>
                            <Tabs.Pane
                                icon={'shield-alt'}
                                className={'hover:text-gray-900 pb-6 border-b border-gray-300'}
                            >
                                Security
                            </Tabs.Pane>
                        </Link>

                        <Link to={ROUTES.settingsTelegram.path}>
                            <Tabs.Pane icon={['fab', 'telegram']} className={'hover:text-gray-900'}>
                                Telegram
                            </Tabs.Pane>
                        </Link>
                    </Tabs>
                </div>
            ) : (
                <div className='p-10 pb-4 pr-8'>
                    <h1 className='text-2xl text-gray-800 font-medium mb-4'>Settings</h1>
                    <Select />
                </div>
            )}

            <div className='h-full flex-grow p-10 pr-0 pl-10 lg:pl-12 lg:pr-0'>
                <Switch>
                    <Route path={ROUTES.settingsAccount.path} component={Account} />
                    <Route path={ROUTES.settingsSecurity.path} component={Security} />
                    <Route path={ROUTES.settingsTelegram.path} component={Telegram} />
                    <Redirect to={ROUTES.settingsAccount.path} />
                </Switch>
            </div>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;

