import PropTypes from 'prop-types';
import { DRAWER_SIDE } from '../../../config/constants';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Drawer = props => {
    const { isVisible, closeDrawer, children, className, side, header, showClose, ...drawerProps } = props;

    const DRAWER_STYLES = {
        [DRAWER_SIDE.left]: ` w-9/12 h-screen bg-white space-y-6 fixed inset-y-0 left-0 transform transition duration-300 ease-in-out z-50 ${
            isVisible ? '-translate-x-0' : '-translate-x-full'
        } ${className}`,

        [DRAWER_SIDE.right]: `w-6/12 h-screen bg-white space-y-6 fixed inset-y-0 right-0  transform transition duration-300 ease-in-out z-50 ${
            isVisible ? '-translate-x-0' : 'translate-x-full'
        } ${className}`,
    };

    return (
        <>
            <div
                className={`bg-black h-full w-full fixed right-0 top-0 z-40 transition-all duration-300 ease-in-out ${
                    isVisible ? 'opacity-50' : 'opacity-0 invisible'
                } `}
                onClick={closeDrawer}
            />

            <div className={`${DRAWER_STYLES[side]} ${className}`}>
                <div className={`py-4 ${header && 'border-b'} `}>
                    <div className='px-8 flex'>
                        {/*<div className='text-xl font-medium flex-grow'>{header}</div>*/}
                        <FontAwesomeIcon
                            icon='times'
                            className='fa-lg text-gray-500 cursor-pointer'
                            onClick={closeDrawer}
                        />
                    </div>
                </div>
                <div className='px-8'>{children}</div>
            </div>
        </>
    );
};

Drawer.propTypes = {
    className: PropTypes.string,
    side: PropTypes.oneOf(Object.values(DRAWER_SIDE)),
};

export default Drawer;
