import PropTypes from 'prop-types';
import { Dialog, Transition } from '@headlessui/react';
import { Fragment, useState } from 'react';

const Modal = props => {
    const { isShowModal, setIsShowModal, children, className, ...modalProps } = props;

    return (
        <Transition appear show={isShowModal} as={Fragment}>
            <div
                className={`w-full h-full fixed top-0 left-0 flex justify-center items-center${
                    isShowModal ? 'hidden' : ''
                } `}
                {...modalProps}
            >
                <div
                    className='w-full h-full absolute top-0 left-0 z-40 bg-black opacity-60'
                    onClick={() => setIsShowModal(!isShowModal)}
                />
                <div
                    className={`bg-white rounded shadow p-8 z-50${className}  ${
                        !isShowModal ? 'scale-100' : 'scale-50'
                    }`}
                >
                    {children}
                </div>
            </div>
        </Transition>
    );
};

Modal.propTypes = {};

export default Modal;
