import PropTypes from 'prop-types';
import { useState } from 'react';
import Input from '../shared/Input/Input';
import Button from '../shared/Button/Button';
import Section from './Section/Section';
import Label from './Label/Label';

const Settings = props => {
    const [isEdited, setIsEdited] = useState(true);

    return (
        <div className='h-full p-8 pb-0 relative overflow-hidden'>
            <div className='relative bg-white flex flex-col  p-20 pb-0 shadow-lg'>
                <Section title={'General'}>
                    <div className='flex flex-col mr-8'>
                        <Label>First Name</Label>
                        <Input
                            type={'text'}
                            placeholder={'First Name'}
                            id={'firstName'}
                            className={'bg-gray-100 text-indigo-700'}
                        />
                    </div>
                    <div className='flex flex-col mr-8'>
                        <Label>Last Name</Label>
                        <Input
                            type={'text'}
                            placeholder={'Last Name'}
                            id={'lastName'}
                            className={'bg-gray-100 text-indigo-700'}
                        />
                    </div>
                    <div className='flex flex-col'>
                        <Label>Email:</Label>
                        <Input
                            type={'email'}
                            placeholder={'Email'}
                            id={'email'}
                            className={'bg-gray-100 text-indigo-700'}
                        />
                    </div>
                </Section>
                <Section title={'Password'}>
                    <div className='mb-5 mt-5'>
                        {/*<div className='flex items-center mb-4'>*/}
                        {/*    <Label className='font-medium text-gray-600 mr-5'>Email:</Label>*/}
                        {/*    <Input*/}
                        {/*        type={'email'}*/}
                        {/*        placeholder={'Email'}*/}
                        {/*        id={'email'}*/}
                        {/*        className={'bg-gray-100 text-indigo-700 mr-10'}*/}
                        {/*    />*/}
                        {/*</div>*/}
                        <div className='flex items-center'>
                            <Label className={'font-semibold text-gray-600 mr-20 -mb-0'}>Password</Label>
                            <Button type={'link'}>Change password</Button>
                            {/*<Input*/}
                            {/*    type={'password'}*/}
                            {/*    placeholder={'Password'}*/}
                            {/*    id={'password'}*/}
                            {/*    className={'bg-gray-100 text-indigo-700'}*/}
                            {/*/>*/}
                        </div>
                    </div>
                </Section>

                <Section title={'Telegram'}></Section>

                <Section title={'Binance API'}>
                    <div className='flex mb-10'>
                        <div className='flex flex-col'>
                            <Label>API key</Label>
                            <Input
                                type={'text'}
                                placeholder={'API key'}
                                id={'apiKey'}
                                className={'bg-gray-100 text-indigo-700 mr-10'}
                            />
                        </div>
                        <div className='flex flex-col'>
                            <Label>Secret Key</Label>
                            <Input
                                type={'text'}
                                placeholder={'Secret Key'}
                                id={'secretKey'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                    </div>
                    <div className='flex'>
                        {isEdited ? (
                            <Button
                                type={'primary'}
                                className={'w-24 rounded-full'}
                                onClick={() => setIsEdited(!isEdited)}
                            >
                                Edit
                            </Button>
                        ) : (
                            <div>
                                <Button type={'link'} className={'mr-8'} onClick={() => setIsEdited(!isEdited)}>
                                    Cancel
                                </Button>
                                <Button
                                    type={'primary'}
                                    className={'w-24 rounded-full'}
                                    onClick={() => setIsEdited(!isEdited)}
                                >
                                    Save
                                </Button>
                            </div>
                        )}
                    </div>
                </Section>
            </div>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;
