import React from 'react';
import PropTypes from 'prop-types';
import logo from '../../assets/images/logo.svg';
import Menu from '../shared/Menu/Menu';
import MenuItem from '../shared/MenuItem/MenuItem';

import menuIcon from '../../assets/icons/MenuItemIcons/MenuItemIcon.svg';

const Sidebar = props => {
    return (
        <div className='w-72 h-full fixed bg-white pt-10'>
            <div className='flex items-center justify-start mx-6'>
                <img className='h-10' src={logo} alt='Logo' />
                <span className='text-gray-600 ml-4 text-2xl font-bold'>Crypto Trader</span>
            </div>

            <Menu className={'mt-20 px-6'}>
                <MenuItem icon={menuIcon}>MenuItem1</MenuItem>
                <Submenu title={'SubMenu1'}></Submenu>
            </Menu>
        </div>
    );
};

Sidebar.propTypes = {};

export default Sidebar;
