import PropTypes from 'prop-types';
import { useState } from 'react';
import Input from '../shared/Input/Input';
import Button from '../shared/Button/Button';
import Section from './Section/Section';
import Label from './Label/Label';

const Settings = props => {
    const [isEdited, setIsEdited] = useState(true);

    return (
        <div className='p-8 pb-0 relative overflow-hidden'>
            <Section title={'General'}>
                <div className='max-w-xl'>
                    <div className='flex flex-col mb-5 max-w-xl xl:mr-8'>
                        <Label>First Name</Label>
                        <Input
                            type={'text'}
                            placeholder={'First Name'}
                            id={'firstName'}
                            className={'bg-gray-100 text-indigo-700'}
                        />
                    </div>
                    <div className='flex flex-col mb-5 max-w-xl xl:mr-8'>
                        <Label>Last Name</Label>
                        <Input
                            type={'text'}
                            placeholder={'Last Name'}
                            id={'lastName'}
                            className={'bg-gray-100 text-indigo-700'}
                        />
                    </div>
                    <div className='flex flex-col max-w-xl mb-8 xl:mr-8'>
                        <Label>Email:</Label>
                        <Input
                            type={'email'}
                            placeholder={'Email'}
                            id={'email'}
                            className={'bg-gray-100 text-indigo-700'}
                        />
                    </div>
                    {isEdited ? (
                        <Button
                            type={'primary'}
                            className={'w-24 rounded-full float-right'}
                            onClick={() => setIsEdited(!isEdited)}
                        >
                            Edit
                        </Button>
                    ) : (
                        <div>
                            <Button type={'link'} className={'mr-8'} onClick={() => setIsEdited(!isEdited)}>
                                Cancel
                            </Button>
                            <Button
                                type={'primary'}
                                className={'w-24 rounded-full'}
                                onClick={() => setIsEdited(!isEdited)}
                            >
                                Save
                            </Button>
                        </div>
                    )}
                </div>
            </Section>

            <Section title={'Password'}>
                <Label className={'text-gray-600 mr-20 -mb-0'}>Password</Label>
                <Button type={'link'}>Change password</Button>
            </Section>

            <Section title={'Telegram'}></Section>

            <Section title={'Binance API'}>
                <div className='flex flex-col mb-5 max-w-xl xl:mr-8'>
                    <Label>API key</Label>
                    <Input
                        type={'text'}
                        placeholder={'API key'}
                        id={'apiKey'}
                        className={'bg-gray-100 text-indigo-700'}
                    />
                </div>
                <div className='flex flex-col max-w-xl mb-8 xl:mr-8'>
                    <Label>Secret Key</Label>
                    <Input
                        type={'text'}
                        placeholder={'Secret Key'}
                        id={'secretKey'}
                        className={'bg-gray-100 text-indigo-700'}
                    />
                </div>
                {isEdited ? (
                    <Button type={'primary'} className={'w-24 rounded-full'} onClick={() => setIsEdited(!isEdited)}>
                        Edit
                    </Button>
                ) : (
                    <div>
                        <Button type={'link'} className={'mr-8'} onClick={() => setIsEdited(!isEdited)}>
                            Cancel
                        </Button>
                        <Button type={'primary'} className={'w-24 rounded-full'} onClick={() => setIsEdited(!isEdited)}>
                            Save
                        </Button>
                    </div>
                )}
            </Section>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;
