import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Button from '../../shared/Button/Button';

const Section = props => {
    const [isEdited, setIsEdited] = useState(true);

    const { title, children, className, ...sectionProps } = props;

    return (
        <section className={`flex flex-col mb-16 ${className}`} {...sectionProps}>
            <h1 className={`text-2xl font-semibold mb-5 ${className}`}>{title}</h1>
            <div className='flex mb-10'>{children}</div>
            <div className='flex'>
                {isEdited ? (
                    <Button type={'primary'} className={'w-24 rounded-full'} onClick={() => setIsEdited(!isEdited)}>
                        Edit
                    </Button>
                ) : (
                    <div>
                        <Button type={'link'} className={'mr-8'} onClick={() => setIsEdited(!isEdited)}>
                            Cancel
                        </Button>
                        <Button type={'primary'} className={'w-24 rounded-full'} onClick={() => setIsEdited(!isEdited)}>
                            Save
                        </Button>
                    </div>
                )}
            </div>
        </section>
    );
};

Section.propTypes = {
    className: PropTypes.string,
};

export default Section;
