import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const MenuItem = props => {
    const { className, itemTitleClass, children, icon, isCollapsed, link, ...menuItemProps } = props;

    return (
        <Link to={link}>
            <div
                className={`hover:text-gray-800 hover:bg-gray-100 cursor-pointer flex items-center p-2 my-6 transition-colors duration-200 rounded-lg ${className} ${
                    isCollapsed ? 'w-9' : ''
                }`}
                {...menuItemProps}
            >
                {icon ? <FontAwesomeIcon icon={icon} className='fa-lg' /> : null}
                <span className={`mx-4 text-lg font-normal ${itemTitleClass}`}>{children}</span>
            </div>
        </Link>
    );
};

MenuItem.propTypes = {
    className: PropTypes.string,
};

export default MenuItem;
