import PropTypes from 'prop-types';
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Button from '../../../shared/Button/Button';
import { data } from './data';

const Telegram = props => {
    const [currentStep, setCurrentStep] = useState(0);

    const { component } = data[currentStep];

    const onCurrentPageNext = () => {
        setCurrentStep(currentStep => currentStep + 1);
    };

    const onCurrentPagePrevious = () => {
        setCurrentStep(currentStep => currentStep - 1);
    };

    const onClickDisconnectButton = e => {
        currentStep === 3 && e.preventDefault();
    };

    return (
        <div className='h-full flex flex-col bg-indigo-600'>
            <h1 className='text-2xl text-gray-800 font-medium pb-6 mb-10 border-b border-gray-300'>Telegram</h1>
            <div className='max-w-3xl pr-10 xl:pr-12 bg-red-300 max-h-full'>
                <div className='bg-white h-96 p-5 bg-green-300 rounded-md shadow flex flex-col justify-between'>
                    <div>
                        <div className='bg-gray-100 xl:flex items-center justify-between rounded-md p-3 mb-5'>
                            <div className='flex items-center'>
                                <span
                                    className={`${
                                        currentStep === 0 ? 'bg-green-400 text-white' : 'bg-white text-indigo-600'
                                    } rounded-full shadow w-8 h-8 font-semibold mr-2 flex justify-center items-center cursor-pointer`}
                                    onClick={() => setCurrentStep(0)}
                                >
                                    1
                                </span>
                                <span
                                    className={`${currentStep === 0 && 'text-green-500'}  cursor-pointer`}
                                    onClick={() => setCurrentStep(0)}
                                >
                                    Start Bot
                                </span>
                            </div>
                            <FontAwesomeIcon
                                icon={currentStep === 0 ? 'grip-lines' : 'ellipsis-h'}
                                className={`${
                                    currentStep === 0 ? 'text-green-500' : 'text-gray-400'
                                } transform rotate-90 xl:rotate-0 ml-2 xl:ml-0`}
                            />

                            <div className='flex items-center'>
                                <span
                                    className={`${
                                        currentStep === 1
                                            ? 'bg-green-400 text-white'
                                            : currentStep === 0
                                            ? 'bg-indigo-600 text-white'
                                            : 'bg-white text-indigo-600 shadow'
                                    } rounded-full w-8 h-8 font-semibold mr-2 flex justify-center items-center cursor-pointer`}
                                    onClick={() => setCurrentStep(1)}
                                >
                                    2
                                </span>
                                <span
                                    className={`${
                                        currentStep === 0
                                            ? 'text-indigo-700'
                                            : currentStep === 1
                                            ? 'text-green-500'
                                            : 'text-gray-800'
                                    }  cursor-pointer`}
                                    onClick={() => setCurrentStep(1)}
                                >
                                    Copy Telegram ID
                                </span>
                            </div>
                            <FontAwesomeIcon
                                icon={currentStep === 1 ? 'grip-lines' : 'ellipsis-h'}
                                className={`${
                                    currentStep === 1 && 'text-green-500'
                                } text-gray-400 transform rotate-90 xl:rotate-0 ml-2 xl:ml-0`}
                            />

                            <div className='flex items-center'>
                                <span
                                    className={`${
                                        currentStep === 1
                                            ? 'bg-indigo-600 text-white'
                                            : currentStep === 2
                                            ? 'bg-green-500 text-white'
                                            : 'bg-white text-indigo-500 shadow'
                                    } rounded-full w-8 h-8  font-semibold mr-2 flex justify-center items-center cursor-pointer`}
                                    onClick={() => setCurrentStep(2)}
                                >
                                    3
                                </span>
                                <span
                                    className={`${
                                        currentStep === 1
                                            ? 'text-indigo-700'
                                            : currentStep === 2
                                            ? 'text-green-500'
                                            : 'text-gray-800'
                                    }  cursor-pointer`}
                                    onClick={() => setCurrentStep(2)}
                                >
                                    Enter Telegram ID
                                </span>
                            </div>
                        </div>

                        {component}
                    </div>

                    <div className='flex justify-center items-center'>
                        {currentStep > 0 && currentStep < 3 && (
                            <Button type={'link'} className={'mr-5'} onClick={onCurrentPagePrevious}>
                                Previous
                            </Button>
                        )}
                        <Button
                            type={'primary'}
                            className={'bg-indigo-600 hover:bg-indigo-700'}
                            onClick={currentStep === 3 ? onClickDisconnectButton : onCurrentPageNext}
                        >
                            {currentStep === 3 ? 'Disconnect' : 'Next'}
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    );
};

Telegram.propTypes = {};

export default Telegram;
