import PropTypes from 'prop-types';
import { Route, useLocation } from 'react-router-dom';
import { ROUTES } from '../../config/constants';
import Login from '../Login/Login';
import Register from '../Register/Register';
import Dashboard from '../Dashboard/Dashboard';
import Layout from '../Layout/Layout';

import { isPrivateRoute } from '../../utils/route';

const routes = [
    {
        path: ROUTES.login.path,
        component: Login,
    },
    {
        path: ROUTES.register.path,
        component: Register,
    },
    {
        path: ROUTES.dashboard.path,
        component: Dashboard,
    },
];

const renderedRoutes = routes.map(route => <Route path={route.path} component={route.component} key={route.path} />);

const Routes = props => {
    let location = useLocation();

    return <>{isPrivateRoute(location.pathname) ? <Layout>{renderedRoutes}</Layout> : { renderedRoutes }}</>;
};

Routes.propTypes = {
    path: PropTypes.oneOf(Object.values(ROUTES)),
};

export default Routes;
