import { useState } from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import arrowDown from '../../../../assets/icons/Arrows/arrow-down.svg';
import Menu from '../Menu';

const SubMenu = props => {
    const [open, setOpen] = useState(true);

    const { title, icon, className, children, toggle, ...subMenuProps } = props;

    return (
        <div>
            <div
                className={`hover:text-gray-800 hover:bg-gray-100 cursor-pointer flex items-center p-2 mt-6 mb-2 transition-colors duration-300 text-gray-400 rounded-lg ${className}`}
                {...subMenuProps}
                onClick={() => setOpen(!open)}
            >
                <img src={icon} alt='icon' className='w-6' />
                <span
                    className={`mx-4 text-lg font-normal ${
                        toggle ? ' opacity-0 invisible' : 'transition-opacity  duration-300'
                    }`}
                >
                    {title}
                </span>
                <span className='flex-grow'>
                    <FontAwesomeIcon
                        icon={`${toggle ? 'chevron-down' : 'chevron-up'}`}
                        className={`float-right  ${
                            toggle ? ' opacity-0 invisible' : 'transition-opacity duration-300'
                        }`}
                    />
                </span>
            </div>
            <div
                className={`border-l-2 border-gray-100 ml-4 pl-4 
                ${toggle ? 'hidden' : ''} ${open ? 'hidden' : ''} `}
            >
                {children}
            </div>
        </div>
    );
};

SubMenu.propTypes = {
    className: PropTypes.string,
};

export default SubMenu;
