import PropTypes from 'prop-types';
import { useState } from 'react';
import { Switch } from '@headlessui/react';

const SwitchButton = props => {
    const [enabled, setEnabled] = useState(false);

    return (
        <div className={'w-full'}>
            <Switch
                checked={enabled}
                onChange={setEnabled}
                className={`${enabled ? 'bg-green-300' : 'bg-gray-300'}
          relative inline-flex flex-shrink-0 h-[34px] w-[34px] border-2 border-transparent rounded-full cursor-pointer transition-colors ease-in-out duration-200 focus:outline-none focus-visible:ring-2  focus-visible:ring-white focus-visible:ring-opacity-75`}
            >
                <span className='sr-only'>Use setting</span>
                <span
                    aria-hidden='true'
                    className={`${enabled ? 'translate-x-9' : 'translate-x-0'}
            pointer-events-none inline-block h-[34px] w-[34px] rounded-full bg-white shadow-lg transform ring-0 transition ease-in-out duration-200`}
                />
            </Switch>
        </div>
    );
};

SwitchButton.propTypes = {};

export default SwitchButton;
