import React from 'react';
import PropTypes from 'prop-types';

const Tooltip = props => {
    const { text, children, className, ...tooltipProps } = props;

    return (
        <div
            className={`bg-gray-700 text-white py-1 text-center min-w-auto max-w-36 rounded ${className}`}
            {...tooltipProps}
        >
            <span>{`Tooltip text`}</span>
            {children}
        </div>
    );
};

Tooltip.propTypes = {};

export default Tooltip;
