import PropTypes from 'prop-types';
import { Route, useLocation } from 'react-router-dom';
import { ROUTES } from '../../config/constants';
import Login from '../Login/Login';
import Register from '../Register/Register';
import Dashboard from '../Dashboard/Dashboard';
import Layout from '../Layout/Layout';

import { isPrivateRoute } from '../../utils/route';

const routes = [
    <Route path={ROUTES.login.path} component={Login} />,
    <Route path={ROUTES.register.path} component={Register} />,
    <Route path={ROUTES.dashboard.path} component={Dashboard} />,
];

const Routes = props => {
    let location = useLocation();

    return (
        <>
            {isPrivateRoute(location.pathname) ? (
                <Layout>
                    {routes.map(route => (
                        <Route path={route.path} component={route.component} key={route.path} />
                    ))}
                </Layout>
            ) : (
                routes.map(route => <Route path={route.path} component={route.component} key={route.path} />)
            )}
        </>
    );
};

Routes.propTypes = {
    path: PropTypes.oneOf(Object.values(ROUTES)),
};

export default Routes;
