import { useState } from 'react';
import PropTypes from 'prop-types';
import { TOOLTIP_POSITION } from '../../../config/constants';

const TOOLTIP_STYLES = {
    [TOOLTIP_POSITION.top]: '-translate-x-24 -translate-y-8',
    [TOOLTIP_POSITION.right]: 'translate-x-2 -translate-y-1',
    [TOOLTIP_POSITION.bottom]: '-translate-x-24 translate-y-6',
    [TOOLTIP_POSITION.left]: '-translate-x-56 -translate-y-1',
};

const Tooltip = props => {
    const [showTooltip, setShowTooltip] = useState(false);

    const { title, position, children, className, ...tooltipProps } = props;

    return (
        <div onMouseEnter={() => setShowTooltip(!showTooltip)} onMouseLeave={() => setShowTooltip(!showTooltip)}>
            {children}
            <span
                className={`bg-gray-700 text-white py-1 text-center w-36 absolute rounded transform transition-all duration-300 ease-in-out ${
                    !showTooltip ? 'opacity-0 invisible' : ''
                }  ${TOOLTIP_STYLES[position]} ${className}`}
                {...tooltipProps}
            >
                {title}
            </span>
        </div>
    );
};

Tooltip.propTypes = {
    className: PropTypes.string,
    text: PropTypes.string.isRequired,
    position: PropTypes.string.isRequired,
};

export default Tooltip;
