import PropTypes from 'prop-types';
import { useState } from 'react';
import Drawer from '../../shared/Drawer/Drawer';
import { DRAWER_SIDE } from '../../../config/constants';
import Tag from '../../shared/Tag/Tag';
import SwitchButton from './SwitchButton/SwitchButton';
import FormItem from '../../shared/Form/FormItem/FormItem';
import Input from '../../shared/Input/Input';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Button from '../../shared/Button/Button';

const EditConfiguration = props => {
    const [isEditing, setIsEditing] = useState(true);

    const { isEditDrawer, closeEditDrawer } = props;

    const onEditConfiguration = () => {
        setIsEditing(!isEditing);
    };

    return (
        <Drawer
            side={DRAWER_SIDE.right}
            isVisible={isEditDrawer}
            closeDrawer={closeEditDrawer}
            header={
                isEditing ? (
                    <Button type={'primary'} className={'bg-indigo-600 p-2 mr-5'} onClick={onEditConfiguration}>
                        Edit
                    </Button>
                ) : (
                    <div>
                        <Button type={'link'} className={'mr-5'} onClick={onEditConfiguration}>
                            Cancel
                        </Button>
                        <Button type={'primary'} className={'bg-indigo-600 p-2 mr-5'} onClick={onEditConfiguration}>
                            Save
                        </Button>
                    </div>
                )
            }
        >
            <div>
                <div className='flex items-center justify-between mb-7 md:mb-12'>
                    <div className='flex items-center'>
                        <h1 className='md:text-2xl font-medium mr-3'>{'Configuration Name'}</h1>
                        <FontAwesomeIcon icon='pencil-alt' className='text-gray-400 fa-sm md:fa-md cursor-pointer' />
                    </div>
                    <Tag color={'green'}>active</Tag>
                </div>

                <div className='mb-8 bg-white p-5 rounded-md shadow'>
                    <div className='flex items-center justify-between mb-6'>
                        <h1 className='text-xl mr-10'>Spot</h1>
                        <SwitchButton />
                    </div>
                    <div className='sm:flex mb-5'>
                        <FormItem
                            label={'Trade Amount'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2 sm:mr-8'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={1} />
                        </FormItem>
                        <FormItem
                            label={'Take Profit Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={0.1} />
                        </FormItem>
                    </div>
                    <div className='sm:flex'>
                        <FormItem
                            label={'Stop Loss Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2 sm:mr-8'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={0.1} />
                        </FormItem>
                        <FormItem
                            label={'Daily Trade Limit'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={1} step={1} />
                        </FormItem>
                    </div>
                </div>

                <div className='mb-8 bg-white p-5 rounded-md shadow'>
                    <div className='flex items-center justify-between mb-6'>
                        <h1 className='text-xl mr-10'>Futures</h1>
                        <SwitchButton />
                    </div>
                    <div className='sm:flex mb-5'>
                        <FormItem
                            label={'Trade Amount'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2 sm:mr-8'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={1} />
                        </FormItem>
                        <FormItem
                            label={'Leverage'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={1} max={125} step={1} />
                        </FormItem>
                    </div>
                    <div className='sm:flex mb-5'>
                        <FormItem
                            label={'Take Profit Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2 sm:mr-8'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={0.1} />
                        </FormItem>
                        <FormItem
                            label={'Stop Loss Percentage'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={0.1} />
                        </FormItem>
                    </div>
                    <div className='sm:flex'>
                        <FormItem
                            label={'Daily Trade Limit'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2 sm:mr-8'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={1} step={1} />
                        </FormItem>
                        <FormItem
                            label={'Manage profit'}
                            helpMessage={'Please fill out this field'}
                            className={'mb-4 sm:mb-0 sm:w-1/2'}
                        >
                            <select
                                className={
                                    'h-8 w-full border border-gray-200 rounded focus:ring focus:ring-indigo-300 px-2 py-1 outline-none'
                                }
                            >
                                <option key={1}>Accumulate profit</option>
                                <option key={2}>Transfer to Spot wallet</option>
                            </select>
                        </FormItem>
                    </div>
                </div>

                <div className='mb-8 bg-white p-5 rounded-md shadow'>
                    <h1 className='text-xl mb-6'>Global</h1>

                    <div className='flex sm:pr-7'>
                        <FormItem
                            label={'Concurrent Trades Limit'}
                            helpMessage={'Please fill out this field'}
                            className={'w-full sm:w-1/2'}
                        >
                            <Input type={'number'} className={'h-8 w-full'} min={1} step={1} />
                        </FormItem>
                    </div>
                </div>
            </div>
        </Drawer>
    );
};

EditConfiguration.propTypes = {};

export default EditConfiguration;
