import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

const MenuItem = props => {
    const { text, icon } = props;
    return (
        <li className='hover:text-gray-800 hover:bg-gray-100 flex items-center p-2 my-6 transition-colors duration-200  text-gray-600 dark:text-gray-400 rounded-lg '>
            {icon ? <img src={icon} alt='item-icon' /> : null}
            <span className='mx-4 text-lg font-normal'>{text}</span>
            <span className='flex-grow text-right'></span>
        </li>
    );
};

MenuItem.propTypes = {};

export default MenuItem;
