import PropTypes from 'prop-types';
import Menu from '../Menu/Menu';

const Drawer = props => {
    const { isCollapsed, setIsCollapsed, ...drawerProps } = props;

    return (
        <>
            <div
                className={`bg-black opacity-60 h-full absolute right-0 top-0 z-40 ${
                    isCollapsed ? 'w-full' : 'hidden'
                } `}
                onClick={() => setIsCollapsed(!isCollapsed)}
            />
            <div
                className={`sidebar bg-white w-9/12 h-screen space-y-6 py-7 px-2 absolute inset-y-0 left-0 transform  md:relative md:translate-x-0 transition duration-300 ease-in-out z-50 ${
                    isCollapsed ? '-translate-x-0' : '-translate-x-full'
                } `}
            >
                <Menu>
                    <Menu.Item>item1</Menu.Item>
                </Menu>
            </div>
        </>
    );
};

Drawer.propTypes = {};

export default Drawer;
