import PropTypes from 'prop-types';
import Input from '../shared/Input/Input';
import Button from '../shared/Button/Button';

const Settings = props => {
    return (
        <div className='bg-indigo-600 h-full px-20 pt-16'>
            <div className='bg-white flex flex-col rounded-2xl p-20'>
                <section className='300 mx-auto w-10/12 mb-16'>
                    <h1 className='text-2xl font-semibold mb-4'>General</h1>
                    <div className='flex justify-between'>
                        <div className='flex flex-col'>
                            <label className='mb-1.5 font-medium text-gray-600'>First Name</label>
                            <Input
                                type={'text'}
                                placeholder={'First Name'}
                                id={'firstName'}
                                className={'bg-gray-100  text-indigo-700'}
                            />
                        </div>
                        <div className='flex flex-col'>
                            <label className='mb-1.5 font-medium text-gray-600'>Last Name</label>
                            <Input
                                type={'text'}
                                placeholder={'Last Name'}
                                id={'lastName'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                    </div>
                </section>
                <section className='300 mx-auto w-10/12 mb-16'>
                    <h1 className='text-2xl font-semibold mb-4'>Security</h1>
                    <div className='flex justify-between'>
                        <div className='flex flex-col'>
                            <label className='mb-1.5 font-medium text-gray-600'>Email</label>
                            <Input
                                type={'email'}
                                placeholder={'Email'}
                                id={'email'}
                                className={'bg-gray-100  text-indigo-700'}
                            />
                        </div>
                        <div className='flex flex-col'>
                            <label className='mb-1.5 font-medium text-gray-600'>Password</label>
                            <Input
                                type={'text'}
                                placeholder={'Last Name'}
                                id={'lastName'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                    </div>
                    <div>
                        <div>
                            <Button type={'primary'} className={'w-64'}>
                                Edit
                            </Button>
                        </div>
                        <div>
                            <Button>Cancel</Button>
                            <Button>Save</Button>
                        </div>
                    </div>
                </section>
                <div></div>
                <div></div>
            </div>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;
