import PropTypes from 'prop-types';
import { useState } from 'react';
import Input from '../shared/Input/Input';
import Button from '../shared/Button/Button';
import Section from './Section/Section';
import Label from './Label/Label';

const Settings = props => {
    const [isEdited, setIsEdited] = useState(true);

    return (
        <div className='p-8 pb-0 relative overflow-hidden'>
            <div className='flex'>
                <Section title={'General'}>
                    <div className='xl:flex items-end'>
                        <div className='flex flex-col mb-5 xl:mb-0 xl:mr-6'>
                            <Label>First Name</Label>
                            <Input
                                type={'text'}
                                placeholder={'First Name'}
                                id={'firstName'}
                                className={'bg-gray-100 text-indigo-700 '}
                            />
                        </div>
                        <div className='flex flex-col mb-5 xl:mb-0 xl:mr-6'>
                            <Label>Last Name</Label>
                            <Input
                                type={'text'}
                                placeholder={'Last Name'}
                                id={'lastName'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                        <div>
                            {isEdited ? (
                                <Button
                                    type={'primary'}
                                    className={'w-24 rounded-full'}
                                    onClick={() => setIsEdited(!isEdited)}
                                >
                                    Edit
                                </Button>
                            ) : (
                                <div>
                                    <Button type={'link'} className={'mr-8'} onClick={() => setIsEdited(!isEdited)}>
                                        Cancel
                                    </Button>
                                    <Button
                                        type={'primary'}
                                        className={'w-24 rounded-full'}
                                        onClick={() => setIsEdited(!isEdited)}
                                    >
                                        Save
                                    </Button>
                                </div>
                            )}
                        </div>
                    </div>
                </Section>

                <Section title={'Password'}>
                    <div className='flex justify-between items-start mb-12'>
                        <div className='flex flex-col'>
                            <Label>Email:</Label>
                            <span>example@gmail.com</span>
                        </div>
                        <Button type={'link'}>Change email</Button>
                    </div>
                    <div className='flex justify-between'>
                        <Label className={'text-gray-600  -mb-0'}>Password</Label>
                        <Button type={'link'}>Change password</Button>
                    </div>
                </Section>
            </div>

            <div className='flex'>
                <Section title={'Binance API'}>
                    <div className='xl:flex xl:items-end'>
                        <div className='flex flex-col mb-5 xl:mb-0 xl:mr-6'>
                            <Label>API key</Label>
                            <Input
                                type={'text'}
                                placeholder={'API key'}
                                id={'apiKey'}
                                className={'bg-gray-100 text-indigo-700 lg:w-full'}
                            />
                        </div>
                        <div className='flex flex-col mb-5 xl:mb-0 xl:mr-6'>
                            <Label>Secret Key</Label>
                            <Input
                                type={'text'}
                                placeholder={'Secret Key'}
                                id={'secretKey'}
                                className={'bg-gray-100 text-indigo-700 lg:w-full'}
                            />
                        </div>
                        <div>
                            {isEdited ? (
                                <Button
                                    type={'primary'}
                                    className={'w-24 rounded-full'}
                                    onClick={() => setIsEdited(!isEdited)}
                                >
                                    Edit
                                </Button>
                            ) : (
                                <div>
                                    <Button type={'link'} className={'mr-8'} onClick={() => setIsEdited(!isEdited)}>
                                        Cancel
                                    </Button>
                                    <Button
                                        type={'primary'}
                                        className={'w-24 rounded-full'}
                                        onClick={() => setIsEdited(!isEdited)}
                                    >
                                        Save
                                    </Button>
                                </div>
                            )}
                        </div>
                    </div>
                </Section>
                <Section title={'Telegram'}></Section>
            </div>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;
