import PropTypes from 'prop-types';
import { useState } from 'react';
import Button from '../../../shared/Button/Button';
import { data } from './data';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Telegram = props => {
    const [currentStep, setCurrentStep] = useState(0);

    const { component } = data[currentStep];

    const onCurrentPageNext = () => {
        setCurrentStep(currentStep => currentStep + 1);
    };

    const onCurrentPagePrevious = () => {
        setCurrentStep(currentStep => currentStep - 1);
    };

    const onClickDisconnectButton = e => {
        currentStep === 3 && e.preventDefault();
    };

    return (
        <div className='h-full flex flex-col'>
            <h1 className='text-2xl text-gray-800 font-medium pb-6 mb-10 border-b border-gray-300'>Telegram</h1>
            <div className='max-w-3xl pr-10 xl:pr-12'>
                <div className='bg-white h-96 p-5 rounded-md shadow flex flex-col justify-between'>
                    <div>
                        <div className='bg-gray-100 flex items-center justify-between rounded-md p-3 mb-5'>
                            <div className='flex items-center'>
                                <span
                                    className={`${
                                        currentStep === 0 && 'bg-green-400 text-white'
                                    } rounded-full shadow w-8 h-8 bg-white mr-2 flex justify-center items-center`}
                                >
                                    1
                                </span>
                                <span className={`${currentStep === 0 && 'text-green-500'}`}>Start Bot</span>
                            </div>

                            <FontAwesomeIcon icon={'ellipsis-h'} />

                            <div className='flex items-center'>
                                <span className='rounded-full shadow w-8 h-8 bg-white mr-2 flex justify-center items-center'>
                                    1
                                </span>
                                <span>Copy Telegram ID</span>
                            </div>
                            <span className='flex self-start'>....</span>
                            <div className='flex items-center'>
                                <span className='rounded-full shadow w-8 h-8 bg-white mr-2 flex justify-center items-center'>
                                    1
                                </span>
                                <span>Enter Telegram ID</span>
                            </div>
                        </div>

                        {component}
                    </div>

                    <div className='flex justify-center items-center'>
                        {currentStep > 0 && currentStep < 3 && (
                            <Button type={'link'} className={'mr-5'} onClick={onCurrentPagePrevious}>
                                Previous
                            </Button>
                        )}
                        <Button
                            type={'primary'}
                            className={'bg-indigo-600 hover:bg-indigo-700'}
                            onClick={currentStep === 3 ? onClickDisconnectButton : onCurrentPageNext}
                        >
                            {currentStep === 3 ? 'Disconnect' : 'Next'}
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    );
};

Telegram.propTypes = {};

export default Telegram;
