import PropTypes from 'prop-types';
import Label from '../../../shared/Label/Label';
import Input from '../../../shared/Input/Input';
import Button from '../../../shared/Button/Button';
import { BUTTON_TYPES } from '../../../../config/constants';
import { useState } from 'react';

const Account = props => {
    const [isEdited, setIsEdited] = useState(true);

    return (
        <div className='h-full flex flex-col'>
            <h1 className='text-2xl text-gray-800 font-medium pb-6 mb-10 border-b border-gray-300'>Account Settings</h1>
            <div className='max-w-3xl pr-10 xl:pr-16'>
                <div className='border-b border-gray-300 pb-16 mb-16'>
                    <h1 className='mb-7 text-xl text-gray-800'>Personal Information</h1>
                    <div className='flex-col'>
                        <div className='lg:flex mb-6'>
                            <div className='flex flex-col lg:w-1/2 lg:mr-6 mb-6 lg:mb-0'>
                                <Label>First Name</Label>
                                <Input
                                    type={'text'}
                                    placeholder={'First Name'}
                                    id={'firstName'}
                                    className={'border-gray-200 text-gray-700 w-full'}
                                />
                            </div>
                            <div className='flex flex-col lg:w-1/2'>
                                <Label>Last Name</Label>
                                <Input
                                    type={'text'}
                                    placeholder={'Last Name'}
                                    id={'lastName'}
                                    className={'border-gray-200 text-gray-700 w-full'}
                                />
                            </div>
                        </div>
                        <div className='flex flex-col'>
                            <Label>Email</Label>
                            <Input
                                type={'email'}
                                placeholder={'Email'}
                                id={'email'}
                                className={'border-gray-200 text-gray-700 w-full'}
                            />
                        </div>
                    </div>
                </div>

                <div>
                    <h1 className='mb-7 text-xl text-black'>Binance API</h1>
                    <div className='lg:flex mb-7'>
                        <div className='flex flex-col lg:w-1/2 lg:mr-6 mb-6'>
                            <Label>API key</Label>
                            <Input
                                type={'text'}
                                placeholder={'API key'}
                                id={'apiKey'}
                                className={'border-gray-200 text-gray-700 w-full'}
                            />
                        </div>
                        <div className='flex flex-col lg:w-1/2'>
                            <Label>Secret Key</Label>
                            <Input
                                type={'text'}
                                placeholder={'Secret Key'}
                                id={'secretKey'}
                                className={'border-gray-200 text-gray-700 w-full'}
                            />
                        </div>
                    </div>
                </div>

                <div className='flex justify-end'>
                    <Button
                        type={BUTTON_TYPES.primary}
                        className={'bg-red-600 hover:bg-red-700 focus:bg-red-700 w-40 justify-self-end'}
                    >
                        Delete Account
                    </Button>
                </div>
            </div>

            <div className='  border-t border-gray-300 pt-6 mt-8'>
                <div className='max-w-3xl pr-10 xl:pr-16 flex justify-end'>
                    {isEdited ? (
                        <Button
                            type={'primary'}
                            className={'w-56 rounded bg-indigo-700'}
                            onClick={() => setIsEdited(!isEdited)}
                        >
                            Edit
                        </Button>
                    ) : (
                        <div className='flex justify-between w-10/12'>
                            <Button type={'link'} className={''} onClick={() => setIsEdited(!isEdited)}>
                                Discard Changes
                            </Button>
                            <Button
                                type={'primary'}
                                className={'w-56 rounded bg-indigo-700'}
                                onClick={() => setIsEdited(!isEdited)}
                            >
                                Save Changes
                            </Button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

Account.propTypes = {};

export default Account;
