import React from 'react';
import PropTypes from 'prop-types';
import Button from '../Button/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { MESSAGE_TYPE } from '../../../config/constants';

const Message = props => {
    const { message, type, className, ...messageProps } = props;
    // const success = () => {
    //     message.success('success');
    // };
    //
    // const error = () => {
    //     message.error('error');
    // };
    //
    // const warning = () => {
    //     message.warning('warning');
    // };

    return (
        <div className={`bg-white shadow-md px-5 py-3 flex items-center absolute top-2 ${className}`} {...messageProps}>
            <FontAwesomeIcon
                icon={
                    MESSAGE_TYPE.success
                        ? 'check-circle'
                        : MESSAGE_TYPE.warning
                        ? 'exclamation-circle'
                        : MESSAGE_TYPE.error && 'times'
                }
                className='fa-lg text-green-500 mr-3'
            />
            <span>{message}</span>
        </div>
    );
};

Message.propTypes = {};

export default Message;
