import PropTypes from 'prop-types';
import Input from '../shared/Input/Input';
import Button from '../shared/Button/Button';
import { useState } from 'react';

const Settings = props => {
    const [isEdited, setIsEdited] = useState(false);

    return (
        <div className='h-full px-28 pt-20 relative overflow-hidden'>
            <div className='relative bg-white flex flex-col rounded-t-3xl p-20 pb-0 shadow-lg'>
                <section className='mb-10'>
                    <h1 className='text-2xl font-semibold mb-4'>General</h1>
                    <div className='flex mb-10'>
                        <div className='flex flex-col mr-8'>
                            <label className='mb-1.5 font-medium text-gray-600'>First Name</label>
                            <Input
                                type={'text'}
                                placeholder={'First Name'}
                                id={'firstName'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                        <div className='flex flex-col mr-8'>
                            <label className='mb-1.5 font-medium text-gray-600'>Last Name</label>
                            <Input
                                type={'text'}
                                placeholder={'Last Name'}
                                id={'lastName'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                        <div className='flex flex-col'>
                            <label className='mb-1.5 font-medium text-gray-600'>Email:</label>
                            <Input
                                type={'email'}
                                placeholder={'Email'}
                                id={'email'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                    </div>
                    <div className='flex'>
                        {isEdited ? (
                            <div>
                                <Button
                                    type={'primary'}
                                    className={'w-24 rounded-full'}
                                    onClick={() => setIsEdited(!isEdited)}
                                >
                                    Edit
                                </Button>
                            </div>
                        ) : (
                            <div>
                                <Button type={'link'} className={'mr-8'}>
                                    Cancel
                                </Button>
                                <Button type={'primary'} className={'w-24 rounded-full'}>
                                    Save
                                </Button>
                            </div>
                        )}
                    </div>
                </section>
                <section className='mb-10'>
                    {/*<h1 className='text-2xl font-semibold mb-4'>Security</h1>*/}
                    <div className='mb-10'>
                        {/*<div className='flex items-center mb-4'>*/}
                        {/*    <label className='font-medium text-gray-600 mr-5'>Email:</label>*/}
                        {/*    <Input*/}
                        {/*        type={'email'}*/}
                        {/*        placeholder={'Email'}*/}
                        {/*        id={'email'}*/}
                        {/*        className={'bg-gray-100 text-indigo-700 mr-10'}*/}
                        {/*    />*/}
                        {/*</div>*/}
                        <div className='flex items-center'>
                            <label className='font-medium text-gray-600 mr-5'>Password</label>
                            <Button type={'link'} className={'p-4 rounded-full mb-0'}>
                                Change password
                            </Button>
                            {/*<Input*/}
                            {/*    type={'password'}*/}
                            {/*    placeholder={'Password'}*/}
                            {/*    id={'password'}*/}
                            {/*    className={'bg-gray-100 text-indigo-700'}*/}
                            {/*/>*/}
                        </div>
                    </div>
                    <div className='flex'>
                        <div>
                            <Button type={'primary'} className={'w-24 rounded-full'}>
                                Edit
                            </Button>
                        </div>
                        <div>
                            <Button type={'link'} className={'mr-8'}>
                                Cancel
                            </Button>
                            <Button type={'primary'} className={'w-24 rounded-full'}>
                                Save
                            </Button>
                        </div>
                    </div>
                </section>
                <section className='mb-10'>
                    <h1 className='text-2xl font-semibold mb-4'>Telegram</h1>
                </section>
                <section className='mb-10'>
                    <h1 className='text-2xl font-semibold mb-4'>Binance API</h1>
                    <div className='flex mb-10'>
                        <div className='flex flex-col'>
                            <label className='mb-1.5 font-medium text-gray-600'>API key</label>
                            <Input
                                type={'text'}
                                placeholder={'API key'}
                                id={'apiKey'}
                                className={'bg-gray-100 text-indigo-700 mr-10'}
                            />
                        </div>
                        <div className='flex flex-col'>
                            <label className='mb-1.5 font-medium text-gray-600'>Secret Key</label>
                            <Input
                                type={'text'}
                                placeholder={'Secret Key'}
                                id={'secretKey'}
                                className={'bg-gray-100 text-indigo-700'}
                            />
                        </div>
                    </div>
                    <div className='flex'>
                        <div>
                            <Button type={'primary'} className={'w-24 rounded-full'}>
                                Edit
                            </Button>
                        </div>
                        <div>
                            <Button type={'link'} className={'mr-8'}>
                                Cancel
                            </Button>
                            <Button type={'primary'} className={'w-24 rounded-full'}>
                                Save
                            </Button>
                        </div>
                    </div>
                </section>
            </div>
        </div>
    );
};

Settings.propTypes = {};

export default Settings;
