import PropTypes from 'prop-types';
import { SIDE } from '../../../config/constants';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Drawer = props => {
    const { isVisible, closeDrawer, children, className, side, title, ...drawerProps } = props;

    const DRAWER_STYLES = {
        [SIDE.left]: ` w-9/12 h-screen bg-white space-y-6 fixed inset-y-0 left-0 transform transition duration-300 ease-in-out z-50 ${
            isVisible ? '-translate-x-0' : '-translate-x-full'
        } ${className}`,

        [SIDE.right]: `w-6/12 h-screen bg-white space-y-6 fixed inset-y-0 right-0  transform transition duration-300 ease-in-out z-50 ${
            isVisible ? '-translate-x-0' : 'translate-x-full'
        } ${className}`,
    };

    return (
        <>
            <div
                className={`bg-black h-full w-full fixed right-0 top-0 z-40 transition-all duration-300 ease-in-out ${
                    isVisible ? 'opacity-50' : 'opacity-0 invisible'
                } `}
                onClick={closeDrawer}
            />

            <div className={`${DRAWER_STYLES[side]} ${className}`}>
                <div className='py-4 border-b'>
                    <div className='pl-6 flex items-center'>
                        <FontAwesomeIcon icon='times' className='fa-lg text-gray-500 mr-5' />
                        <h1 className='text-xl font-medium'>{'title'}</h1>
                    </div>
                </div>
                {children}
            </div>
        </>
    );
};

Drawer.propTypes = {
    className: PropTypes.string,
    side: PropTypes.oneOf(Object.values(SIDE)),
};

export default Drawer;
