import PropTypes from 'prop-types';

const Button = props => {
    const { children, className, type, ...buttonProps } = props;

    const BUTTON_STYLES = {
        primary: `bg-indigo-600 w-full py-2 mb-6 rounded text-white font-medium hover:bg-indigo-700 hover:shadow-md focus:bg-indigo-700 focus:ring-2 focus:ring-indigo-500 focus:shadow-inner ${className} ${type} `,
        link: 'text-indigo-500 font-medium',
    };

    return (
        <button className={className} type={type} {...buttonProps}>
            {children}
        </button>
    );
};

Button.propTypes = {
    classname: PropTypes.string,
    type: PropTypes.string,
};

export default Button;
