import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import logo from '../../assets/images/logo.svg';
import loginBackground from '../../assets/images/login-background.svg';
import Input from '../shared/Input/Input';
import Button from '../shared/Button/Button';
import LinkButton from '../shared/LinkButton/LinkButton';

const Login = props => {
    return (
        // h-screen md:h-auto
        <div className='bg-gray-200 flex '>
            <div className='hidden md:block bg-indigo-500 w-full'>
                <div
                    style={{ backgroundImage: `url(${loginBackground})` }}
                    className='w-11/12 h-screen bg-no-repeat bg-center bg-contain mx-auto'
                />
            </div>
            <div className='bg-white w-full md:max-w-sm lg:max-w-lg 2xl:max-w-3xl'>
                <div className='w-10/12 h-full mx-auto max-w-sm pt-8 pb-4 flex flex-col justify-between'>
                    <div className='flex items-center'>
                        <img src={logo} alt='Logo' className='w-1/6 mr-2.5' />
                        <h1 className='text-3xl md:text-4xl font-bold text-gray-800'>Crypto Trader</h1>
                    </div>
                    <div>
                        <h2 className='text-3xl md:text-4xl text-gray-700 font-bold tracking-wide mb-20'>Login</h2>
                        <div className='flex flex-col mb-4'>
                            <label className='mb-1.5 font-medium text-gray-600'>Email</label>
                            <Input type={'text'} id={'email'} placeholder={'Email'} />
                            <p className='text-red-500 text-xs mt-1'>Please fill out this field.</p>
                        </div>
                        <div className='flex flex-col mb-8 '>
                            <label className='mb-1.5 font-medium text-gray-600 '>Password</label>
                            <Input type={'password'} id={'password'} placeholder={'Password'} />
                            <p className='text-red-500 text-xs mt-1'>Please fill out this field.</p>
                        </div>
                        <Button>{'Sign in'}</Button>
                        <div className='text-center mb-3'>
                            <Link to='/reset-password'>
                                <LinkButton>{'Reset Password?'}</LinkButton>
                            </Link>
                        </div>
                    </div>
                    <div className='text-center text-gray-700 text-lg font-medium'>
                        <span className='mr-1'>Don't have an account yet?</span>
                        <Link to='/register'>
                            <LinkButton>{'Join now'}</LinkButton>
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

Login.propTypes = {};

export default Login;
