import { useState } from 'react';
import PropTypes from 'prop-types';
import Button from '../../../shared/Button/Button';
import { BUTTON_TYPES, MESSAGE_TYPE } from '../../../../config/constants';
import Message from '../../../shared/Message/Message';

const Security = props => {
    const [isEditing, setIsEditing] = useState(true);

    const onEditPassword = () => {
        setIsEditing(!isEditing);
    };

    return (
        <div className='h-full flex flex-col'>
            <h1 className='text-2xl text-gray-800 font-medium pb-6 mb-10 border-b border-gray-300'>Security</h1>
            <div className='max-w-3xl pr-10 xl:pr-16'>
                <div className='flex justify-between items-center bg-white p-5 rounded-md'>
                    <span>Password</span>
                    <Message type={warning} message={'asasas'} />
                    {isEditing ? (
                        <Button type={BUTTON_TYPES.primary} className={'bg-indigo-600 w-32'} onClick={onEditPassword}>
                            Edit
                        </Button>
                    ) : (
                        <div>
                            <Button type={BUTTON_TYPES.link} className={'mr-5'} onClick={onEditPassword}>
                                Cancel
                            </Button>
                            <Button
                                type={BUTTON_TYPES.primary}
                                className={'bg-indigo-600 w-32'}
                                onClick={onEditPassword}
                            >
                                Save
                            </Button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

Security.propTypes = {};

export default Security;
