import Menu from '../Menu/Menu';

const Drawer = props => {
    const { isCollapsed, setIsCollapsed, ...drawerProps } = props;

    return (
        <>
            <div
                className={`bg-black opacity-60 h-full absolute right-0 top-0 z-40 ${isCollapsed ? 'w-0' : 'w-full'} `}
            />
            <div className='absolute left-0 top-0 h-full bg-white z-50  w-9/12 shadow'>
                <Menu>
                    <Menu.Item>item1</Menu.Item>
                </Menu>
            </div>
        </>
    );
};

Drawer.propTypes = {};

export default Drawer;
