import React from 'react';
import PropTypes from 'prop-types';
import { STATUS } from '../../../config/constants';

const TAG_STYLES = {
    [STATUS.active]: 'bg-green-500',
};

const Tag = props => {
    const { color, children, ...tagProps } = props;

    return (
        <span className={`bg-${color}-200 py-1 px-2`} {...tagProps}>
            {children}
        </span>
    );
};

Tag.propTypes = {};

export default Tag;
