// Auth
export const AUTH_USER_LOGGED_IN = 'auth/user/logged-in';
export const AUTH_USER_LOGGED_OUT = 'auth/user/logged-out';
export const SET_CURRENT_USER = 'auth/user/changed';

// Components
export const SHOW_MESSAGE_COMPONENT = 'components/message/added';
export const DESTROY_MESSAGE_COMPONENT = 'components/message/destroyed';
export const DESTROY_ALL_MESSAGE_COMPONENTS = 'components/messages/destroyed';

// Global
export const APP_LOADING_CHANGED = 'global/app-loading/changed';
export const SIDEBAR_COLLAPSED_STATE_CHANGED = 'global/sidebar/collapsed/changed';
export const SIDEBAR_COLLAPSED_STATE_TOGGLED = 'global/sidebar/collapsed/toggled';
