import AuthService from '../../services/AuthService';
import * as TYPES from '../types';
import { setAuthToken } from '../../utils/auth';
import { showMessageComponent } from './components';
import { MESSAGE_TYPE } from '../../config/constants';

export const setUserLoggedIn = user => ({
    type: TYPES.AUTH_USER_LOGGED_IN,
    payload: user,
});

export const setCurrentUser = user => ({
    type: TYPES.SET_CURRENT_USER,
    payload: user,
});

export const login = userData => async dispatch => {
    try {
        const response = await AuthService.login(userData);

        const { token, ...user } = response.data;

        setAuthToken(token);

        dispatch(setUserLoggedIn(user));

        return user;
    } catch (error) {
        const errorResponseMessage = error.response?.data.error.message;

        console.error(errorResponseMessage || error);

        dispatch(showMessageComponent(MESSAGE_TYPE.error, errorResponseMessage));
    }
};

export const register = userData => async dispatch => {
    try {
        const response = await AuthService.register(userData);

        const { token, ...user } = response.data;

        setAuthToken(token);

        dispatch(setUserLoggedIn(user));

        return user;
    } catch (error) {
        const errorResponseMessage = error.response?.data.error.message;

        console.error(errorResponseMessage || error);

        dispatch(showMessageComponent(MESSAGE_TYPE.error, errorResponseMessage));
    }
};

export const fetchCurrentUser = () => async dispatch => {
    try {
        const response = await AuthService.fetchCurrentUser();

        const user = response.data;

        dispatch(setCurrentUser(user));

        return user;
    } catch (error) {
        console.error(error.response?.data.error.message || error);
    }
};
