import React from 'react';
import PropTypes from 'prop-types';
import logo from '../../assets/images/logo.svg';
import arrowLeft from '../../assets/icons/Arrows/arrow-left.svg';
import arrowRight from '../../assets/icons/Arrows/arrow-right.svg';
import Menu from '../shared/Menu/Menu';

import menuIcon from '../../assets/icons/MenuItemIcons/MenuItemIcon.svg'; // for test

const Sidebar = props => {
    return (
        <div className='w-72 h-full fixed bg-white pt-10 shadow'>
            <img
                src={arrowLeft}
                className='w-8 border border-transparent rounded-full shadow p-1 bg-white absolute left-64 ml-4'
                alt='icon'
            />

            <div className='flex items-center justify-start mx-6'>
                <img className='h-10' src={logo} alt='Logo' />
                <span className='text-gray-600 ml-4 text-2xl font-bold'>Crypto Trader</span>
            </div>

            <Menu className={'mt-11 px-6'}>
                <Menu.Item icon={menuIcon}>MenuItem1</Menu.Item>
                <Menu.SubMenu title={'SubMenu1'} icon={menuIcon}>
                    <Menu.Item>SubMenuItem1</Menu.Item>
                </Menu.SubMenu>
            </Menu>
        </div>
    );
};

Sidebar.propTypes = {};

export default Sidebar;
